#include <bits/stdc++.h>
using namespace std;

#define FastIO cin.tie(0)->sync_with_stdio(0)
#define endl "\n"

using ll = long long;

ll num;
ll sum;
void input() {
	cin >> num;
}
void solve() {
	sum += num;
}
int main(void) {
	FastIO;
	while(num>=0) {
		input();
		solve();
	}
	cout << sum + 1 << endl;
	return 0;
}
